import React, { useState, useEffect, useContext, useRef } from "react";
import { useHistory } from "react-router-dom";
import { Link } from 'react-router-dom';
import UserContext from "../context/UserContext";
import ImageDownload from '../components/imageDownload';
// import ColoredInImage from "./images/coloredInImage";
import axios from 'axios';
import {saveSvgAsPng} from 'save-svg-as-png';
import swal from 'sweetalert';
import Stitch from "../components/images/stitch.js";
import Minnie from "../components/images/minnie.js";

export default function UserImageCard(props) {
    const [image, setImage] = useState({});
    var ColorImage = useRef();
    const { userData } = useContext(UserContext);
    const history = useHistory();

    // let image = props.userImage
    // console.log(image)
    // const history = useHistory();

    // const showFeatured = () => {
    //     history.push(`/featuredimage/${props.userImage.id}`)   
    // }
    // const components = {
    //     "stitch": Stitch
    // }

    useEffect(() => {
        setImage(props.userImage)
        // console.log(image)
        // ColorImage.current = components[image.component]
        // console.log(ColorImage.current)
    }, [props])

    // const fetchItems = () => {
    //     setImage(props.userImage)
    //     console.log(image)
    //     const ColorImage = components[image.component]
    // }

    //    const ColorImage = components[image.component]

    const deleteImage = async (id) => {
        if (!userData.token) {
            history.push("/")
            return
        }
        const response = await axios.delete(
            `http://localhost:5000/user_images/${id}`,
            { headers: { "x-auth-token": userData.token } },
        );
        console.log(response.data._id)
        history.push(`/coloringpage/${response.data._id}`)
    }

    const {goBack} = this.props.navigation;

    const saveImage = async (image) => {
        saveSvgAsPng(`imge_${image}`, `${image}.png`);
        // eslint-disable-next-line no-lone-blocks
        {goBack()}
        // swal("Download Complete", "This image has been saved to your Downloads", "success");
    }

    return (
        <div className="image-card">
            {image.component === "stitch" ?
            <div>
                <Stitch component={image.component} fillColors={image.fill_colors} />
                <Link to={"/coloringpage/"+image._id}>Edit</Link> | 
                <a href="#" onClick={() => deleteImage(image._id)}>Delete</a>
                <Link to={"/imageDownload/"+image._id}>Download</Link>
                {/* <a href="#" onClick={() => saveImage("stitch")}>Save</a> */}
            </div> :
            <>Hello</>
            }
        </div>
    )

}


