import React, { useState, useEffect, useContext, useRef } from "react";
import { useParams, useHistory } from 'react-router-dom';
import { Link } from 'react-router-dom';
import ColoredInImage from "./images/coloredInImage";
import UserContext from "../context/UserContext";
// import ColoredInImage from "./images/coloredInImage";
import axios from 'axios';
import { saveSvgAsPng } from 'save-svg-as-png';
import swal from 'sweetalert';
import Stitch from "../components/images/stitch.js";
import Minnie from "../components/images/minnie.js";

export default function UserImageCard(props) {
  let { id } = useParams();
  const [image, setImage] = useState({});
  const { userData } = useContext(UserContext);
  const [fetched, setFetched] = useState(false);
  const history = useHistory();

  useEffect(() => {
    // getting user image object from database using the object id
    const fetchItems = async () => {
      if (!fetched) {
        const response = await axios.get(
          `http://localhost:5000/user_images/${id}`,
          { headers: { "x-auth-token": userData.token } },
        );
        // set the user image 
        setImage(response.data)
        // setColorImage(components[userImage.component])
        console.log(response.data)
        setFetched(true)
      }
    }
    fetchItems();
  })

  const saveImage = async () => {
    // <Link to={"/imageDownload/"+image._id}>Download</Link> | 
    // saveSvgAsPng(document.getElementById(`img_${image.component}`), `${image.component}.png`);
    swal("Download Complete", `img_${image.component}`, "success");
  }

  return (
    <div className="image-card">
      swal("Download Complete", "img_{image.component}, "success");
      <ColoredInImage size="large" key={image._id} component={image.component} fillColors={image.fill_colors}/>
      {/* {saveImage()} */}
    </div>
  )
}
