import React, { useState, useEffect, useContext, useRef } from "react";
import UserContext from "../context/UserContext";
import axios from 'axios';
//import ImageCard from '../components/imageCard';
import UserImageCard from '../components/userImageCard';
// import { all } from "../../backend/routes/image";

export default function UserGallery() {
    const [allImages, addImage] = useState([{}]);
    const { userData } = useContext(UserContext);

    useEffect(() => {
        const fetchItems = async () => {
            const response = await axios.get(
                "http://localhost:5000/user_images/all",
                { headers: { "x-auth-token": userData.token } },
            );
            addImage(response.data);
            // console.log(allImages);
        }
        fetchItems();
    })



    return (
        <>
        <h1>{userData.user.username}'s Gallery</h1>
        <div class="image-gallery">
            {allImages.map((image, i) => {
                return (
                    <>
                    <UserImageCard size="small" userImage={image} />
                    <div class="btn-group">
                    <button class="download" onClick={() => downloadImage()}>Download</button>
                    <button class="delete" onClick={() => deleteImage(image._id)}>Delete</button>
                    <button class="color-me" onClick={() => editImage()}>Edit</button>
                </div>
                </>
                )
            }
            )
            }
        </div>
        </>

        // <div>
        // <h1>{userData.user.username}'s Gallery</h1>
        //     <div id="my-gallery">
        //         <div class="row">
        //             {allImages.map((image, i) => {
        //                 return (
        //                     <UserImageCard size="small" userImage={image} />
        //                 )
        //             })}
        //         </div>
        //     </div>
        // </div>
    );
}
