import React, { useState, useEffect, useContext, useRef } from "react";
import UserContext from "../context/UserContext";
import { useHistory } from "react-router-dom";
import axios from 'axios';
//import ImageCard from '../components/imageCard';
import UserImageCard from '../components/userImageCard';
// import { all } from "../../backend/routes/image";

export default function UserGallery() {
    //const images = useRef([{}]);
    const [allImages, addImage] = useState([{}]);
    const { userData } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {
        const fetchItems = async () => {
            const response = await axios.get(
                "http://localhost:5000/user_images/all",
                { headers: { "x-auth-token": userData.token } },
            );
            /*             images.current = response.data; */
            addImage(response.data);
            // console.log(allImages);
        }
        fetchItems();
    })

    const deleteImage = async (id) => {
        // if (!userData.token) {
        //     history.push("/")
        //     return
        // }

        console.log(id)
        const response = await axios.delete(
            "http://localhost:5000/user_images/" + id,
            { headers: { "x-auth-token": userData.token } },
        );

        this.setState({
            allImages: allImages.filter(el => el._id !== id)
        })
        console.log(response.data._id)
        history.push(`/coloringpage/${response.data._id}`)
    }

    return (
        <div id="my-gallery">
            {/* {images.current.map((image, i) => { */}
            {allImages.map((image, i) => {
                // console.log(image)
                return (
                    <div>
                        <UserImageCard id="my-image-card" key={image._id} userImage={image} />
                    </div>
                )
            })}
        </div>
    );
}

