import React, {useEffect, useContext, Fragment} from "react";
import PopoverPicker from "../components/PopoverPicker";

export default function ColorPalette(props) {

   const colors = ['#C53151', '#FF355E', '#FF00CC', '#FF6037', '#FFAE42', '#FFFF66', '#93DFB8', '#66FF66', '#319177', '#00CCCC','#00468C','#652DC1','#FFCBA4','#C8C8CD']

    return(
        <Fragment>
            {colors.map(color => { 
                // looping through all of the colors and highlights a color in palette if selected 
                const activeClass = props.currentColor === color ? 
                    'color-swatch-active' : 
                    '';
                return (
                    // color is changed 
                    <div className="color-palette">
                    <div onClick={() => {props.changeColor(color)}}>
                        <div className={`color-swatch ${activeClass}`}  style={{backgroundColor: color}}></div>
                        {/* <PopoverPicker color={color} onChange={props.changeColor(color)} />; */}

                    </div>
                    </div>
                )
            })}

            </Fragment>
    )
}
