import React, { useState, useEffect, useContext } from "react";
import { useParams, useHistory } from 'react-router-dom';
import ColoredInImage from "./images/coloredInImage";
import ColoringPage from "../components/coloringPage";
import UserContext from "../context/UserContext";
// import ColoredInImage from "./images/coloredInImage";
import axios from 'axios';
import { saveSvgAsPng } from 'save-svg-as-png';
import swal from 'sweetalert';

export default function (props) {
  let { id } = useParams();
  const [image, setImage] = useState({});
  const { userData } = useContext(UserContext);
  const [fetched, setFetched] = useState(false);
  const history = useHistory();

  useEffect(() => {
    // getting user image object from database using the object id
    const fetchItems = async () => {
      if (!fetched) {
        const response = await axios.get(
          `http://localhost:5000/user_images/${id}`,
          { headers: { "x-auth-token": userData.token } },
        );
        // set the user image 
        setImage(response.data)
        console.log(response.data)
        setFetched(true)
      }
    }
    fetchItems();
  })

  const saveImage = () => {
    saveSvgAsPng(document.getElementById("your-image"), "your-image.png");
    swal("Download Complete", "This image has been saved to your Downloads", "success");
  }

  return (
    <div className="image-card">
      <ColoringPage></ColoringPage>
      {saveImage()}
      {/* {history.push('/userGallery')} */}
    </div>
  )
}
