import React, { useState, useEffect, useContext, useRef } from "react";
import { useHistory } from "react-router-dom";
import { Link } from 'react-router-dom';
import UserContext from "../context/UserContext";
import axios from 'axios';
import swal from 'sweetalert';
import ColoredInImage from "./images/coloredInImage";
export default function UserImageCard(props) {
    const [image, setImage] = useState({});
    const { userData } = useContext(UserContext);
    const history = useHistory();
    const [fetched, setFetched] = useState(false);


    // useEffect(() => {
    //     setImage(props.userImage)
    // }, [props])

    useEffect(() => {
        const fetchItems = async () => {
            const response = await axios.get(
                `http://localhost:5000/user_images/${props.userImage._id}`,
                { headers: { "x-auth-token": userData.token } },
            );
            setImage(response.data);
            console.log(props.userImage.imageId);
        }
        fetchItems();
    })

    const downloadImage = () => {
        history.push(`/imageDownload/${image._id}`)
    }

    const deleteImage = async () => {
        if (!userData.token) {
            history.push("/userGallery")
            return
        }
        swal({
            title: "Delete image?",
            text: "This cannot be reversed.",
            buttons: true
        })
            .then(async (willDelete) => {
                if (willDelete) {
                    await axios.delete(
                        `http://localhost:5000/user_images/${image._id}`,
                        { headers: { "x-auth-token": userData.token } },
                    );
                }
            });
    }

    const editImage = () => {
        history.push(`/coloringpage/${image._id}`)
    }

    const changePublicity = async () => {
        image.public === false ? image.public = true : image.public = false
        console.log(image.public)

        setImage ({
            ...image
        })

        if (!userData.token) {
            history.push("/userGallery")
            return
        }


                await axios.patch(
                    `http://localhost:5000/user_images/${image._id}`,
                    image,
                    { headers: { "x-auth-token": userData.token } },
                );
                console.log(image);


    }

    return (
        <div className="image-card">
            {image.component !== undefined ?
                <>
                    <ColoredInImage component={image.component} size={props.size} onFill={() => { }} fillColors={image.fill_colors} />
                    <div class="btn-group" id="user">
                        <button class="download" onClick={() => downloadImage()}>Download</button>
                        <button class="delete" onClick={() => deleteImage()}>Delete</button>
                        <button class="color-me" onClick={() => editImage()}>Edit</button>
                        <button class="public" onClick={() => changePublicity()}>Publicize</button>
                    </div>
                </>
                : <>Loading</>
            }
        </div>

        // <div className="col-4 mx-4 my-4 mt-auto">
        //     {image.component !== undefined ?
        //             <>
        //             <ColoredInImage component={image.component} size={props.size} onFill={() => { }} fillColors={image.fill_colors} />
        //             <div class="btn-group">
        //                 <button class="download" onClick={() => downloadImage()}>Download</button>
        //                 <button class="delete" onClick={() => deleteImage(image._id)}>Delete</button>
        //                 <button class="color-me" onClick={() => editImage()}>Edit</button>
        //             </div>
        //             </>
        //         : <>Loading</>
        //     }
        // </div>

    )
}
