import React, { useState, useEffect, useContext } from "react";
import { useParams, useHistory } from 'react-router-dom';
import ColoredInImage from "./images/coloredInImage";
import UserContext from "../context/UserContext";
// import ColoredInImage from "./images/coloredInImage";
import axios from 'axios';
import { saveSvgAsPng } from 'save-svg-as-png';
import swal from 'sweetalert';

export default function ImageDownload(props) {
  let { id } = useParams();
  const [userImage, setUserImage] = useState({});
  const { userData } = useContext(UserContext);
  const [fetched, setFetched] = useState(false);
  const history = useHistory();

  useEffect(() => {
    // getting user image object from database using the object id
    const fetchItems = async () => {
      if (!fetched) {
        const response = await axios.get(
          `http://localhost:5000/user_images/${id}`,
          { headers: { "x-auth-token": userData.token } },
        );
        // set the user image 
        setUserImage(response.data)
        // document.getElementById("button").click()
        setFetched(true)
      }
    }
    fetchItems();
  })

  const saveImage = () => {
    swal({
      title: "Are you sure?",
      text: "Once deleted, you will not be able to recover this image",
      icon: "warning",
      buttons: true,
      dangerMode: true,
  })
  .then((willDelete) => {
      if (willDelete) {
          fetch(`https://color-by-nature-api.herokuapp.com/user_images/${id}`,{
              method: 'DELETE',
              headers: {'Authorization': `bearer ${token}`}
          })
          .then(r => r.json())
          .then(data => {
              dispatch({
                  type: 'SET_IMAGES',
                  payload: data
              });
              swal("Image Deleted", "This image has been removed from your gallery", "success");
              history.push(`/mygallery`)
          })        
      }
    });

    saveSvgAsPng(document.getElementById("your-image"), "your-image.png");


  }


  return (
    <div className="image-card">
      <ColoredInImage size="large" key={userImage._id} component={userImage.component} fillColors={userImage.fill_colors} />
      {/* <button id="button" onClick={saveImage}>Download</button> */}
      {saveImage()}
    </div>
  )
}
